package ch02;

public class BitOperationTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=5;
		int num2=10;
		
		System.out.println(num|num2);
		System.out.println(num&num2);
		System.out.println(num^num2);
		System.out.println(~num);
		
		System.out.println(num<<2);
		System.out.println(num);
		System.out.println(num<<=2);
		System.out.println(num);
		
		
	}

}
